import React from 'react';

const options = [
    { id: 1, value: 'chicken', protein: 27 },
    { id: 2, value: 'eggs', protein: 13 },
    { id: 3, value: 'cheese piątnica semi-fat', protein: 16 },
    { id: 4, value: 'peanut butter', protein: 23.78 }
]

class UserPanel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            weight: 0,
            protein: 0,
            total: 0,
            id: 0,
            name: '',
            foods: []
        };
        this.handleWeightChange = this.handleWeightChange.bind(this);
        this.handleProteinChange = this.handleProteinChange.bind(this);
        this.handleNameChange = this.handleNameChange.bind(this);
    }

    addObjectToArray = e => {
        e.preventDefault();
        let proteinPerFood = (this.state.weight * this.state.protein) / 100
        this.setState({
            foods: [
                ...this.state.foods,
                { id: this.state.id + 1, name: this.state.name, protein: proteinPerFood }
            ],
            name: '',
            protein: 0,
            weight: 0,
            id: this.state.id + 1,
            total: this.state.total + proteinPerFood
        })
    };

    handleWeightChange(e) {
        this.setState({
            weight: e.target.value
        });
    }

    handleProteinChange(e) {
        this.setState({
            protein: e.target.value
        });
    }

    handleNameChange(e) {
        let n = e.target.value
        this.setState({
            name: n
        })
        let obj = options.find(o => o.value === n);
        this.setState({
            protein: obj.protein
        });
    }

    render() {
        return <>
            <h2>Total Protein {this.state.total}g</h2>

            <label>food:</label>
            <input
                id="multiselect"
                type="text"
                name=""
                list="productName"
                onChange={this.handleNameChange}
                value={this.state.name} />
            <datalist id="productName">
                {options.map(option => {
                    return (
                        <option key={option.id} value={option.value}>{option.value}</option>
                    );
                })}
            </datalist>
            <label>weight (grams):</label>
            <input
                name="weight"
                value={this.state.weight}
                onChange={this.handleWeightChange} />
            <label>protein/100g:</label>
            <input
                name="protein"
                value={this.state.protein}
                onChange={this.handleProteinChange} />
            {this.state.foods.map(food => {
                return (
                    <div key={food.id}>
                        <p>food: {food.name} protein: {food.protein}g</p>
                        <hr />
                    </div>
                );
            })}

            <button
                className="add-button"
                onClick={this.addObjectToArray}>
                Add food
            </button>
        </>
    }
}

export default UserPanel;
