import { useState } from 'react';

export default function Total(props) {

    const [ingredients, setIngredients] = useState([]);

    // ✅ Add an object to a state array
    const addObjectToArray = e => {
        // alert(JSON.stringify(props))
        e.preventDefault();
        let obj = { name: 'this.props.name', protein: 'this.props.protein' }
        console.log(props)
        setIngredients(current => [...current, obj]);
    };

    // ✅ Update one or more objects in a state array
    // const updateObjectInArray = () => {
    //     setIngredients(current =>
    //         current.map(obj => {
    //             if (obj.id === 2) {
    //                 return { ...obj, name: 'Sophia', protein: 'Sweden' };
    //             }

    //             return obj;
    //         }),
    //     );
    // };

    // ✅ Remove one or more objects from state array
    const removeObjectFromArray = (e) => {
        e.preventDefault();
        setIngredients(current =>
            current.filter(obj => {
                return obj.id !== 2;
            }),
        );
    };

    return (
        <div>
            <button onClick={addObjectToArray}>
                Add ingredient
            </button>

            {/* <button onClick={updateObjectInArray}>Update object in array</button> */}

            <button onClick={removeObjectFromArray}>Remove object from array</button>

            {ingredients.map(ingredient => {
                return (
                    <div key={ingredient.id}>
                        <p>name: {ingredient.name}</p>
                        <p>protein: {ingredient.protein}</p>

                        <hr />
                    </div>
                );
            })}
        </div>
    );
}
