import React from 'react';

class UserPanel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            weight: 0,
            protein: 0,
            total: 0,
            name: '',
            ingredients: []
        };
        this.handleWeightChange = this.handleWeightChange.bind(this);
        this.handleProteinChange = this.handleProteinChange.bind(this);
        this.handleNameChange = this.handleNameChange.bind(this);
    }

    addObjectToArray = e => {
        // alert("cool")
        e.preventDefault();
        let obj = { name: this.state.name, protein: this.state.total }
        this.setState({
            ingredients: [
                ...this.state.ingredients,
                { name: this.state.name, protein: (this.state.weight * this.state.protein) / 100 }
            ],
            name: '',
            protein: 0,
            weight: 0
        })
    };

    handleWeightChange(e) {
        this.setState({
            weight: e.target.value
        });
    }

    handleProteinChange(e) {
        this.setState({
            protein: e.target.value
        });
    }

    handleNameChange(e) {
        this.setState({
            name: e.target.value
        })
    }

    render() {
        return <>
            <label>
                nazwa:
                <input
                    name="name"
                    value={this.state.name}
                    onChange={this.handleNameChange} />
            </label>
            <label>
                weight (grams):
                <input
                    name="weight"
                    value={this.state.weight}
                    onChange={this.handleWeightChange} />
            </label>
            <label>
                protein/100g:
                <input
                    name="protein"
                    value={this.state.protein}
                    onChange={this.handleProteinChange} />
            </label>
            {/* <Total total={(this.state.weight * this.state.protein) / 100} name={this.state.name} /> */}
            {/* <button className="">+</button> */}
            {this.state.ingredients.map(ingredient => {
                return (
                    <div key={ingredient.id}>
                        <p>food: {ingredient.name} protein: {ingredient.protein}g</p>
                        <hr />
                    </div>
                );
            })}

            <button className="add-button" onClick={this.addObjectToArray}>
                Add ingredient
            </button>
        </>
    }
}

export default UserPanel;
