import { useState } from 'react';

export default function Total(props) {
    const initialState = [
        { id: 1, name: '', totalProtein: 0 }
    ];

    const [ingredients, setIngredient] = useState(initialState);

    // ✅ Add an object to a state array
    const addObjectToArray = obj => {
        setIngredient(current => [...current, obj]);
    };

    // ✅ Update one or more objects in a state array
    // const updateObjectInArray = () => {
    //     setIngredient(current =>
    //         current.map(obj => {
    //             if (obj.id === 2) {
    //                 return { ...obj, name: 'Sophia', totalProtein: 'Sweden' };
    //             }

    //             return obj;
    //         }),
    //     );
    // };

    // ✅ Remove one or more objects from state array
    const removeObjectFromArray = (e) => {
        e.preventDefault();
        setIngredient(current =>
            current.filter(obj => {
                return obj.id !== 2;
            }),
        );
    };

    return (
        <>
            <button
                onClick={() =>
                    addObjectToArray({
                        id: Math.random(),
                        name: 'Carl',
                        totalProtein: 'Canada',
                    })
                }
            >
                Add ingredient
            </button>

            {/* <button onClick={updateObjectInArray}>Update object in array</button> */}

            <button onClick={removeObjectFromArray}>Remove object from array</button>

            {ingredients.map(ingredient => {
                return (
                    <div key={ingredient.id}>
                        <p>name: {ingredient.name}</p>
                        <p>totalProtein: {ingredient.totalProtein}</p>
                        <hr />
                    </div>
                );
            })}
        </>
    );
}
