        });
    }

    render() {
        return <>
            <form onSubmit={this.handleSubmit}>
                <label>
                    weight (grams):
                    <input
                        name="weight"
                        value={this.state.weight}
                        onChange={this.handleInputChange} />
                </label>
                <label>
                    protein:
                    <input
                        name="protein"
                        value={this.state.protein}
                        onChange={this.handleInputChange} />
                </label>
                <Total total={this.state.total} />
                <button type="submit">Submit</button>

            </form>
        </>;
    }
}

export default UserPanel;
